import React, { useState } from 'react';
import { Api } from './Api';

const ITEMS_PER_PAGE = 50;

const App = () => {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [previousFilterResult, setPreviousFilterResult] = useState(null);
  const [filter, setFilter] = useState({
    name: '',
    price: '',
    brand: ''
  });

  const nextPage = () => {
    setCurrentPage(prevPage => Math.min(prevPage + 1, totalPages));
  };

  const prevPage = () => {
    setCurrentPage(prevPage => Math.max(prevPage - 1, 1));
  };

  const handleSubmit = async event => {
    event.preventDefault();

    try {
      const api = new Api('https://api.valantis.store:41000/', 'Valantis');
      let filterParams = {};
      let filteredIds = [];

      // Проверяем, какие параметры фильтра заданы
      if (filter.name !== '' && filter.price === '' && filter.brand === '') {
        filterParams.product = filter.name;
        filteredIds = await api.filter(filterParams);
      } else if (filter.name === '' && filter.price !== '' && filter.brand !== '') {
        filterParams.price = parseFloat(filter.price);
        filteredIds = await api.filter(filterParams);
      } else if (filter.name !== '') {
        filterParams.product = filter.name;
        filteredIds = await api.filter(filterParams);
      }

      if (Array.isArray(filteredIds) && filteredIds.length > 0) {
        const filteredItems = await api.getItems(filteredIds);
        const uniqueItems = filteredItems.filter((item, index, self) => {
          const isFirstOccurrence = index === self.findIndex((t) => t.id === item.id);
          return isFirstOccurrence;
        });

        setTotalPages(Math.ceil(uniqueItems.length / ITEMS_PER_PAGE));
        setProducts(uniqueItems);
        setCurrentPage(1);
        setPreviousFilterResult(uniqueItems);
      } else if (previousFilterResult !== null) {
        // Если нет данных от сервера, но есть предыдущие результаты фильтрации, используем их
        const filteredItems = applyClientFilter(previousFilterResult);
        setTotalPages(Math.ceil(filteredItems.length / ITEMS_PER_PAGE));
        setProducts(filteredItems);
        setCurrentPage(1);
      }
    } catch (error) {
      console.error('Ошибка при получении данных:', error);
    }
  };

  // Функция для применения фильтрации на стороне клиента
  const applyClientFilter = items => {
    let filteredItems = items.slice();

    if (filter.price !== '') {
      const priceFilter = parseFloat(filter.price);
      filteredItems = filteredItems.filter(item => parseFloat(item.price) === priceFilter);
    }

    if (filter.brand !== '') {
      filteredItems = filteredItems.filter(item => item.brand === filter.brand);
    }

    return filteredItems;
  };

  const handleInputChange = event => {
    const { name, value } = event.target;
    setFilter(prevFilter => ({
      ...prevFilter,
      [name]: value
    }));
  };

  const handleKeyPress = event => {
    if (event.key === 'Enter') {
      handleSubmit(event);
    }
  };

  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;
  const currentProducts = products.slice(startIndex, endIndex);

  return (
    <div>
      <h1>Список продуктов</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Название"
          name="name"
          value={filter.name}
          onChange={handleInputChange}
        />
        <input
          type="text"
          placeholder="Цена"
          name="price"
          value={filter.price}
          onChange={handleInputChange}
        />
        <input
          type="text"
          placeholder="Бренд"
          name="brand"
          value={filter.brand}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
        />
        <button type="submit">Применить фильтр</button>
      </form>
      <ul>
        {currentProducts.map(product => (
          <li key={product.id}>
            <div>ID: {product.id}</div>
            <div>Название: {product.product}</div>
            <div>Цена: {product.price}</div>
            <div>Бренд: {product.brand}</div>
          </li>
        ))}
      </ul>
      <div>
        <button onClick={prevPage} disabled={currentPage === 1}>Предыдущая страница</button>
        <span> Страница {currentPage} из {totalPages} </span>
        <button onClick={nextPage} disabled={currentPage === totalPages}>Следующая страница</button>
      </div>
    </div>
  );
};

export default App;
