import React, { useState } from 'react';
import { Api } from './Api';

const ITEMS_PER_PAGE = 50;

const App = () => {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [previousFilter, setPreviousFilter] = useState({});
  const [previousFilterResult, setPreviousFilterResult] = useState(null);
  const [hasMadeServerRequest, setHasMadeServerRequest] = useState(false);

  const nextPage = () => {
    setCurrentPage(prevPage => Math.min(prevPage + 1, totalPages));
  };

  const prevPage = () => {
    setCurrentPage(prevPage => Math.max(prevPage - 1, 1));
  };

  const handleSubmit = async event => {
    event.preventDefault();

    try {
      const api = new Api('https://api.valantis.store:41000/', 'Valantis');
      let filterParams = {};
      let filteredIds = [];

      if (filterHasChanged(previousFilter, filter)) {
        if (filter.name !== '') {
          filterParams.product = filter.name;
          filteredIds = await api.filter(filterParams);
        } else if (filter.price !== '' && filter.brand !== '') {
          filterParams.price = parseFloat(filter.price);
          filteredIds = await api.filter(filterParams);
        } else if (filter.price !== '') {
          filterParams.price = parseFloat(filter.price);
          filteredIds = await api.filter(filterParams);
        } else if (filter.brand !== '') {
          filterParams.brand = filter.brand;
          filteredIds = await api.filter(filterParams);
        }
      } else if (previousFilterResult !== null && hasMadeServerRequest) {
        filteredIds = previousFilterResult.map(item => item.id);
      }

      if (Array.isArray(filteredIds) && filteredIds.length > 0) {
        const filteredItems = await api.getItems(filteredIds);
        const uniqueItems = filteredItems.filter((item, index, self) => {
          const isFirstOccurrence = index === self.findIndex((t) => t.id === item.id);
          return isFirstOccurrence;
        });

        setTotalPages(Math.ceil(uniqueItems.length / ITEMS_PER_PAGE));
        setProducts(uniqueItems);
        setCurrentPage(1);
        setPreviousFilter(filter);
        setPreviousFilterResult(uniqueItems);
        setHasMadeServerRequest(true);
      }
    } catch (error) {
      console.error('Ошибка при получении данных:', error);
    }
  };

  const filterHasChanged = (previousFilter, currentFilter) => {
    const keys = Object.keys(previousFilter);
    for (let key of keys) {
      if (previousFilter[key] !== currentFilter[key]) {
        return true;
      }
    }
    return false;
  };

  const handleInputChange = event => {
    const { name, value } = event.target;
    setPreviousFilterResult(null);
    setProducts([]);
    setCurrentPage(1);
    setFilter(prevFilter => ({
      ...prevFilter,
      [name]: value
    }));
  };

  const handleKeyPress = event => {
    if (event.key === 'Enter') {
      handleSubmit(event);
    }
  };

  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;
  const currentProducts = products.slice(startIndex, endIndex);

  return (
    <div>
      <h1>Список продуктов</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Название"
          name="name"
          value={filter.name}
          onChange={handleInputChange}
        />
        <input
          type="text"
          placeholder="Цена"
          name="price"
          value={filter.price}
          onChange={handleInputChange}
        />
        <input
          type="text"
          placeholder="Бренд"
          name="brand"
          value={filter.brand}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
        />
        <button type="submit">Применить фильтр</button>
      </form>
      <ul>
        {currentProducts.map(product => (
          <li key={product.id}>
            <div>ID: {product.id}</div>
            <div>Название: {product.product}</div>
            <div>Цена: {product.price}</div>
            <div>Бренд: {product.brand}</div>
          </li>
        ))}
      </ul>
      <div>
        <button onClick={prevPage} disabled={currentPage === 1}>Предыдущая страница</button>
        <span> Страница {currentPage} из {totalPages} </span>
        <button onClick={nextPage} disabled={currentPage === totalPages}>Следующая страница</button>
      </div>
    </div>
  );
};

export default App;
