import React, { useState, useEffect } from 'react';
import Api from './Api';

const ITEMS_PER_PAGE = 50;

const App = () => {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const api = new Api('https://api.valantis.store:41000/', 'Valantis');
        const ids = await api.getIds();
        const items = await api.getItems(ids);
        setTotalPages(Math.ceil(items.length / ITEMS_PER_PAGE));
        setProducts(items);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const nextPage = () => {
    setCurrentPage(prevPage => Math.min(prevPage + 1, totalPages));
  };

  const prevPage = () => {
    setCurrentPage(prevPage => Math.max(prevPage - 1, 1));
  };

  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;
  const currentProducts = products.slice(startIndex, endIndex);

  return (
    <div>
      <h1>Product List</h1>
      <ul>
        {currentProducts.map(product => (
          <li key={product.id}>
            <div>ID: {product.id}</div>
            <div>Name: {product.product}</div>
            <div>Price: {product.price}</div>
            <div>Brand: {product.brand}</div>
          </li>
        ))}
      </ul>
      <div>
        <button onClick={prevPage} disabled={currentPage === 1}>Previous Page</button>
        <span> Page {currentPage} of {totalPages} </span>
        <button onClick={nextPage} disabled={currentPage === totalPages}>Next Page</button>
      </div>
    </div>
  );
};

export default App;
