import React, { useState, useEffect } from 'react';
import { Api } from './Api';

const ITEMS_PER_PAGE = 50;

const App = () => {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [filter, setFilter] = useState({
    name: '',
    minPrice: '',
    maxPrice: '',
    brand: ''
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const api = new Api('https://api.valantis.store:41000/', 'Valantis');
        const ids = await api.getIds();
        let items = await api.getItems(ids);

        // Удаление дубликатов
        const uniqueItems = items.filter((item, index, self) => {
          const isFirstOccurrence = index === self.findIndex((t) => t.id === item.id);
          if (!isFirstOccurrence) {
            console.log("Элемент с неуникальным id:", item);
          }
          return isFirstOccurrence;
        });

        setTotalPages(Math.ceil(uniqueItems.length / ITEMS_PER_PAGE));
        setProducts(uniqueItems);
      } catch (error) {
        console.error('Ошибка при получении данных:', error);
      }
    };

    fetchData();
  }, []);

  const handleSubmit = event => {
    event.preventDefault();
    // Применение фильтров
    const filteredProducts = products.filter(product => {
      const nameMatch = product.product.toLowerCase().includes(filter.name.toLowerCase());
      const brandMatch = product.brand.toLowerCase().includes(filter.brand.toLowerCase());
      const minPriceMatch = filter.minPrice === '' || product.price >= parseFloat(filter.minPrice);
      const maxPriceMatch = filter.maxPrice === '' || product.price <= parseFloat(filter.maxPrice);
      return nameMatch && brandMatch && minPriceMatch && maxPriceMatch;
    });
    setTotalPages(Math.ceil(filteredProducts.length / ITEMS_PER_PAGE));
    setProducts(filteredProducts);
    setCurrentPage(1);
  };

  const handleFilterChange = event => {
    const { name, value } = event.target;
    setFilter(prevFilter => ({
      ...prevFilter,
      [name]: value
    }));
  };

  const nextPage = () => {
    setCurrentPage(prevPage => Math.min(prevPage + 1, totalPages));
  };

  const prevPage = () => {
    setCurrentPage(prevPage => Math.max(prevPage - 1, 1));
  };

  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;
  const currentProducts = products.slice(startIndex, endIndex);

  return (
    <div>
      <h1>Список продуктов</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Название:
          <input type="text" name="name" value={filter.name} onChange={handleFilterChange} />
        </label>
        <label>
          Минимальная цена:
          <input type="number" name="minPrice" value={filter.minPrice} onChange={handleFilterChange} />
        </label>
        <label>
          Максимальная цена:
          <input type="number" name="maxPrice" value={filter.maxPrice} onChange={handleFilterChange} />
        </label>
        <label>
          Бренд:
          <input type="text" name="brand" value={filter.brand} onChange={handleFilterChange} />
        </label>
        <button type="submit">Применить фильтры</button>
      </form>
      <ul>
        {currentProducts.map(product => (
          <li key={product.id}>
            <div>ID: {product.id}</div>
            <div>Название: {product.product}</div>
            <div>Цена: {product.price}</div>
            <div>Бренд: {product.brand}</div>
          </li>
        ))}
      </ul>
      <div>
        <button onClick={prevPage} disabled={currentPage === 1}>Предыдущая страница</button>
        <span> Страница {currentPage} из {totalPages} </span>
        <button onClick={nextPage} disabled={currentPage === totalPages}>Следующая страница</button>
      </div>
    </div>
  );
};

export default App;
