import React, { useState, useEffect } from 'react';
import { Api } from './Api';

const ITEMS_PER_PAGE = 50;

const App = () => {
  const [originalProducts, setOriginalProducts] = useState([]);
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [filter, setFilter] = useState({
    name: '',
    priceMin: '',
    priceMax: '',
    brand: ''
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const api = new Api('https://api.valantis.store:41000/', 'Valantis');
        const ids = await api.getIds();
        let items = await api.getItems(ids);

        const uniqueItems = items.filter((item, index, self) => {
          const isFirstOccurrence = index === self.findIndex((t) => t.id === item.id);
          if (!isFirstOccurrence) {
            console.log("Элемент с неуникальным id:", item);
          }
          return isFirstOccurrence;
        });

        setOriginalProducts(uniqueItems);
        setTotalPages(Math.ceil(uniqueItems.length / ITEMS_PER_PAGE));
        setProducts(uniqueItems);
      } catch (error) {
        console.error('Ошибка при получении данных:', error);
      }
    };

    fetchData();
  }, []);

  const nextPage = () => {
    setCurrentPage(prevPage => Math.min(prevPage + 1, totalPages));
  };

  const prevPage = () => {
    setCurrentPage(prevPage => Math.max(prevPage - 1, 1));
  };

  const handleSubmit = event => {
    event.preventDefault();

    const filteredItems = originalProducts.filter(item => {
      const nameMatch = filter.name === '' || item.product.toLowerCase().includes(filter.name.toLowerCase());
      let priceMatch = true;
      if (filter.priceMin !== '' || filter.priceMax !== '') {
        const priceMin = filter.priceMin === '' ? 0 : parseFloat(filter.priceMin);
        const priceMax = filter.priceMax === '' ? Infinity : parseFloat(filter.priceMax);
        priceMatch = item.price >= priceMin && item.price <= priceMax;
      }
      const brandMatch = filter.brand === '' || item.brand.toLowerCase().includes(filter.brand.toLowerCase());
      return nameMatch && priceMatch && brandMatch;
    });

    setProducts(filteredItems);
    setTotalPages(Math.ceil(filteredItems.length / ITEMS_PER_PAGE));
    setCurrentPage(1);
  };

  const handleInputChange = event => {
    const { name, value } = event.target;
    setFilter(prevFilter => ({
      ...prevFilter,
      [name]: value
    }));
  };

  const handleKeyPress = event => {
    if (event.key === 'Enter') {
      handleSubmit(event);
    }
  };

  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;
  const currentProducts = products.slice(startIndex, endIndex);

  return (
    <div>
      <h1>Список продуктов</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Название"
          name="name"
          value={filter.name}
          onChange={handleInputChange}
        />
        <input
          type="text"
          placeholder="Цена от"
          name="priceMin"
          value={filter.priceMin}
          onChange={handleInputChange}
        />
        <input
          type="text"
          placeholder="Цена до"
          name="priceMax"
          value={filter.priceMax}
          onChange={handleInputChange}
        />
        <input
          type="text"
          placeholder="Бренд"
          name="brand"
          value={filter.brand}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
        />
        <button type="submit">Применить фильтр</button>
      </form>
      <ul>
        {currentProducts.map(product => (
          <li key={product.id}>
            <div>ID: {product.id}</div>
            <div>Название: {product.product}</div>
            <div>Цена: {product.price}</div>
            <div>Бренд: {product.brand}</div>
          </li>
        ))}
      </ul>
      <div>
        <button onClick={prevPage} disabled={currentPage === 1}>Предыдущая страница</button>
        <span> Страница {currentPage} из {totalPages} </span>
        <button onClick={nextPage} disabled={currentPage === totalPages}>Следующая страница</button>
      </div>
    </div>
  );
};

export default App;
